import logo from './logo.svg';
import './App.css';
import React from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      sisaUang : 0,
      persentaseUang : 0,
      pemasukanUang : 0,
      pengeluaranUang : 0,
      transaksiIN : 0,
      transaksiOut : 0,
      summary : [
        {
          deskripsi : 'Menerima Gaji',
          tanggal : '1 Janaurai 2023',
          nominal : 1000000,
          category : 'IN'
        },
        {
          deskripsi : 'Makan nasi padag',
          tanggal : '2 Janaurai 2023',
          nominal : 20000,
          category : 'OUT'
        }
      ]
    }
  }

  render(){
    return (
      <>
        <div className='container py-5'>
          <div className='row'>
            <div className='col-12 text-center'>
              <h1 className='fw-bold'>FEECUANEN APPS</h1>
              <hr className='w-75 mx-auto' />
              <h2 className='fw-bold'>Rp. {this.state.sisaUang},-</h2>
              <span className='title-md'>Sisa uang kamu tersisa {this.state.persentaseUang}% lagi</span>
            </div>
          </div>

          <div className='row mt-4'>
            <div className='col-6'>
              <div className='card-wrapper p-4'>
                <div className='icon-wrapper mb-1'>
                  <i class="bi bi-wallet2"></i>
                </div>
                <span className='title-sm'>Pemasukan</span>
                <h3 className='fw-bold'>Rp. {this.state.pemasukanUang},-</h3>
                <div>
                <span className='title-sm text-ungu fw-bold'>50</span><span className='title-sm'> Transaksi</span>
                </div>
              </div>
            </div>

            <div className='col-6'>
              <div className='card-wrapper p-4'>
                <div className='icon-wrapper mb-1'>
                <i class="bi bi-cash-stack"></i>
                </div>
                <span className='title-sm'>Pengeluaran</span>
                <h3 className='fw-bold'>Rp. {this.state.pengeluaranUang},-</h3>
                <div>
                <span className='title-sm text-ungu fw-bold'>50</span><span className='title-sm'> Transaksi</span>
                </div>
              </div>
            </div>

          </div>

          <div className='row mt-5'>
            <div className='col-12 d-flex justify-content-between align-item-center'>
              <h4>Ringkasan Transaksi</h4>
              <div className='wrapper-button d-flex'>
                <ModalCreate />
                <button className='button btn-ungu px-3 py-2 me-2'>Pemasukan <i class="bi bi-plus-circle"></i></button>
                <button className='button btn-pink'>Pengeluaran <i class="bi bi-dash-circle-fill"></i></button>
              </div>
            </div>
          </div>

          <div className='row mt-4'>
            {this.state.summary.map((sum)=> {
              return (
                <div className='col-12 d-flex justify-content-between align-item-center'>
                  <div className='d-flex'>
                    <div className={sum.category === 'IN' ? 'icon-wrapper-IN' : 'icon-wrapper-OUT'}>
                    <i class={sum.category === 'IN' ? 'bi bi-wallet2' : 'bi bi-bag-dash'}></i>
                    </div>

                    <div className='transaction ms-2 d-flex flex-column'>
                      <h6>{sum.deskripsi}</h6>
                      <span className='title'>{sum.tanggal}</span>
                      </div>
                  </div>

                  <h5 className={sum.category === 'IN' ? 'text-money-in' : 'text-money-out'}>Rp. {sum.nominal}</h5>
                </div>
              )
            }) }
          </div>
        </div>
      </>  
    )
  }
}

class ModalCreate extends React.Component {
  constructor() {
    super();
    this.state = {
      show : false
    }

    this
  }

  handleClose() {
    this.setState({
      show : false
    })
  }

  handleShow() {
    this.setState({
      show : true
    })
  }

  render() {
    return (
      <>
        <Button variant="primary" onClick={this.handleShow}>
          Launch demo modal
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={this.handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
    </>
    )
  }
}

export default App;
